(2893, 335, '_faq_0_faq_number', 'field_64a55b2d9ff93'),
(2894, 335, 'faq_0_faq_title', 'How can I immigrate to Canada? \r\n'),
(2895, 335, '_faq_0_faq_title', 'field_64a55b369ff94'),
(2896, 335, 'faq_0_faq_description', 'There are various immigration pathways to Canada, including economic programs, family sponsorship, refugee programs, and more. Each pathway has specific requirements and eligibility criteria.'),
