(2905, 335, '_faq_1_faq_number', 'field_64a55b2d9ff93'),
(2906, 335, 'faq_1_faq_title', 'What is Express Entry?'),
(2907, 335, '_faq_1_faq_title', 'field_64a55b369ff94'),
(2908, 335, 'faq_1_faq_description', 'Express Entry is an online system used to manage applications for Canada\'s main economic immigration programs. It includes the Federal Skilled Worker Program, the Federal Skilled Trades Program, and the Canadian Experience Class.'),
(2909, 335, '_faq_1_faq_description', 'field_64a55b659ff95'),
