(2919, 335, '_faq_2_faq_number', 'field_64a55b2d9ff93'),
(2920, 335, 'faq_2_faq_title', 'What are the requirements for Express Entry?'),
(2921, 335, '_faq_2_faq_title', 'field_64a55b369ff94'),
(2922, 335, 'faq_2_faq_description', 'The requirements for Express Entry include meeting the eligibility criteria of one of the three economic programs, creating an online profile, and receiving an Invitation to Apply (ITA) based on factors such as age, education, work experience, language proficiency, and adaptability.'),
(2923, 335, '_faq_2_faq_description', 'field_64a55b659ff95'),
