(2931, 335, '_faq_3_faq_number', 'field_64a55b2d9ff93'),
(2932, 335, 'faq_3_faq_title', 'Can I apply for Canadian citizenship directly? '),
(2933, 335, '_faq_3_faq_title', 'field_64a55b369ff94'),
(2934, 335, 'faq_3_faq_description', 'No, you cannot apply for Canadian citizenship directly. You must first become a permanent resident of Canada, meet the residency requirements, and then apply for citizenship after a specific period of time.'),
(2935, 335, '_faq_3_faq_description', 'field_64a55b659ff95'),
