(2943, 335, '_faq_4_faq_number', 'field_64a55b2d9ff93'),
(2944, 335, 'faq_4_faq_title', 'What is the Provincial Nominee Program (PNP)? '),
(2945, 335, '_faq_4_faq_title', 'field_64a55b369ff94'),
(2946, 335, 'faq_4_faq_description', 'The Provincial Nominee Program allows provinces and territories in Canada to nominate individuals who have the skills and experience needed in their specific region. Each province or territory has its own unique nomination criteria and streams.'),
(2947, 335, '_faq_4_faq_description', 'field_64a55b659ff95'),
(2948, 335, 'faq_5_faq_id_', 'headingSix'),
(2949, 335, '_faq_5_faq_id_', 'field_64a55af89ff90'),
