(2955, 335, '_faq_5_faq_number', 'field_64a55b2d9ff93'),
(2956, 335, 'faq_5_faq_title', 'Can I bring my family members to Canada?'),
(2957, 335, '_faq_5_faq_title', 'field_64a55b369ff94'),
(2958, 335, 'faq_5_faq_description', 'Yes, you can bring your eligible family members to Canada through various programs, including family sponsorship. Spouses, common-law partners, dependent children, and parents or grandparents may be eligible for sponsorship.'),
(2959, 335, '_faq_5_faq_description', 'field_64a55b659ff95'),
