(2967, 335, '_faq_6_faq_number', 'field_64a55b2d9ff93'),
(2968, 335, 'faq_6_faq_title', 'What is the Canadian Experience Class (CEC)?'),
(2969, 335, '_faq_6_faq_title', 'field_64a55b369ff94'),
(2970, 335, 'faq_6_faq_description', 'The Canadian Experience Class is an immigration program that allows individuals with Canadian work experience to apply for permanent residence. It is one of the economic programs managed through Express Entry.'),
(2971, 335, '_faq_6_faq_description', 'field_64a55b659ff95'),
