(2991, 335, '_faq_8_faq_number', 'field_64a55b2d9ff93'),
(2992, 335, 'faq_8_faq_title', 'What is a study permit? '),
(2993, 335, '_faq_8_faq_title', 'field_64a55b369ff94'),
(2994, 335, 'faq_8_faq_description', 'A study permit is a document issued by Immigration, Refugees and Citizenship Canada (IRCC) that allows foreign nationals to study at designated learning institutions in Canada. It is usually required for full-time programs longer than six months.'),
