(3003, 335, '_faq_9_faq_number', 'field_64a55b2d9ff93'),
(3004, 335, 'faq_9_faq_title', 'Can international students work in Canada?'),
(3005, 335, '_faq_9_faq_title', 'field_64a55b369ff94'),
(3006, 335, 'faq_9_faq_description', 'Yes, international students with a valid study permit may be eligible to work in Canada. They can work on-campus without a separate work permit and may also be eligible for off-campus work permits or co-op/internship work permits.'),
