(3015, 335, '_faq_10_faq_number', 'field_64a55b2d9ff93'),
(3016, 335, 'faq_10_faq_title', 'What is the Canadian Citizenship Test?'),
(3017, 335, '_faq_10_faq_title', 'field_64a55b369ff94'),
(3018, 335, 'faq_10_faq_description', 'The Canadian Citizenship Test is a test that assesses your knowledge of Canada, including its history, geography, government, rights, and responsibilities. It is part of the citizenship application process.'),
(3019, 335, '_faq_10_faq_description', 'field_64a55b659ff95'),
