(3028, 335, 'faq_11_faq_title', 'How long does it take to process an immigration application? \r\n'),
(3029, 335, '_faq_11_faq_title', 'field_64a55b369ff94'),
(3030, 335, 'faq_11_faq_description', 'Processing times vary depending on the type of immigration program and the volume of applications. It\'s advisable to check the IRCC website for the most up-to-date processing times.'),
(3031, 335, '_faq_11_faq_description', 'field_64a55b659ff95'),
