(3039, 335, '_faq_12_faq_number', 'field_64a55b2d9ff93'),
(3040, 335, 'faq_12_faq_title', 'Can I apply for refugee status in Canada?'),
(3041, 335, '_faq_12_faq_title', 'field_64a55b369ff94'),
(3042, 335, 'faq_12_faq_description', 'Yes, individuals who fear persecution or face risks in their home country may be eligible to apply for refugee status in Canada. The refugee process is separate from other immigration programs and is based on humanitarian grounds.\r\n'),
(3043, 335, '_faq_12_faq_description', 'field_64a55b659ff95'),
