(3087, 335, '_faq_16_faq_number', 'field_64a55b2d9ff93'),
(3088, 335, 'faq_16_faq_title', 'What is the Medical Exam requirement for immigration? '),
(3089, 335, '_faq_16_faq_title', 'field_64a55b369ff94'),
(3090, 335, 'faq_16_faq_description', 'In some cases, immigration applicants are required to undergo a medical examination to assess their health and admissibility to Canada. This requirement helps protect the health and safety of Canadians.'),
(3091, 335, '_faq_16_faq_description', 'field_64a55b659ff95'),
