(3099, 335, '_faq_17_faq_number', 'field_64a55b2d9ff93'),
(3100, 335, 'faq_17_faq_title', 'Can I appeal a refusal of my immigration application?'),
(3101, 335, '_faq_17_faq_title', 'field_64a55b369ff94'),
(3102, 335, 'faq_17_faq_description', 'Yes, in certain circumstances, you can appeal a refusal of your immigration application. The process and eligibility for an appeal vary depending on the specific program and the grounds of refusal.'),
(3103, 335, '_faq_17_faq_description', 'field_64a55b659ff95'),
