(3111, 335, '_faq_18_faq_number', 'field_64a55b2d9ff93'),
(3112, 335, 'faq_18_faq_title', 'How can I check the status of my immigration application? '),
(3113, 335, '_faq_18_faq_title', 'field_64a55b369ff94'),
(3114, 335, 'faq_18_faq_description', 'You can check the status of your immigration application online through the IRCC website. The system allows you to track the progress and receive updates on your application.'),
