(3856, 116, '_service_details_card_0_card_title', 'field_64a7e6031bb8e'),
(3857, 116, 'service_details_card_0_card_content', 'Our lawyers assist individuals and families in creating comprehensive estate plans tailored to their unique circumstances. We guide you through the process of drafting wills, establishing trusts, and implementing other essential estate planning documents. Our goal is to minimize potential disputes, reduce tax liabilities, and ensure the smooth transfer of assets to your chosen beneficiaries.\r\n\r\n'),
(3858, 116, '_service_details_card_0_card_content', 'field_64a7e60f1bb8f'),
(3859, 116, 'service_details_card_1_card_title', 'Will Drafting and Probate:'),
(3860, 116, '_service_details_card_1_card_title', 'field_64a7e6031bb8e'),
